  {% if post.hidden == true or post.sitemap == false %}
    {% continue %}
  {% endif %}

  {{ site.url }}{{ post.url }}
  {% if post.seo.date_modified %}
    {{ post.seo.date_modified | date_to_xmlschema }}
  {% else %}
    {{ post.date | date_to_xmlschema }}
  {% endif %}

{% endfor %}

